mapper.select:
SELECT u.uid, u.uname , r.id as ROLE_ID
from td_user as u , td_user_role as r 
where u.uid=r.uid and uid = #{uid,jdbcType=INTEGER}

select 

from td_user
where 1=1 

    and ustatus=#{ustatus,jdbcType=TINYINT}


    and utype=#{utype,jdbcType=TINYINT}


    and name=#{name,jdbcType=VARCHAR}

select count(uid)
from td_user
where 1=1 

    and ustatus=#{ustatus,jdbcType=TINYINT}


    and utype=#{utype,jdbcType=TINYINT}


    and name=#{name,jdbcType=VARCHAR}
